$(function(){$("#display").after('<ul id="nav">').cycle({fx:"fade",speed:"fast",timeout:0,pager:"#nav",pagerAnchorBuilder:function(a,n){return'<li><a href="#"></a></li>'}})
var a=$("#secondary-nav").offset().top
$(window).on("scroll",function(){window.scrollY>=a-25&&$("#secondary-nav").addClass("scroll"),window.scrollY<a-26&&$("#secondary-nav").removeClass("scroll")})})
